module Task6 where
import Data.List (nub, findIndex)
input = ""

fours (a:b:c:d:xs) = [a, b, c, d] : fours (b:c:d:xs)
fours a = []
answer1 =
    let f = fours input
        Just i = findIndex (\x -> 4 == (length $ nub x)) f
    in  i + 4

parts n xs =
    let part = take 14 xs
    in  if length part == 14
        then part : parts n (tail xs)
        else []

answer2 =
    let f = parts 14 input
        Just i = findIndex (\x -> 14 == (length $ nub x)) f
    in  i + 14